import { actions } from './Theme.actions';
import { dark } from '../../styles/Themes'

type Action = {
  type: keyof typeof actions,
  payload: Object
};

export default function themeReducer (state: Object = dark, action: Action) {
  switch (action.type) {
    case actions.DARK:
      return action.payload;
    case actions.LIGHT:
      return action.payload;
    default:
      return state;
  }
}
