import styled from 'styled-components';

/**
  * Layout
  * HD = Header
  * SB = Sidebar
  * RT = Routes
  */

export const Grid = styled.div`
  display: grid;
  grid-template-columns: 250px auto;
  grid-template-rows: 70px auto;

  grid-template-areas:
  'SB HD'
  'SB RT';

  height: 100vh;
`;
